        <div className="app">
            <h1>MovieLand</h1>

            <div className="search">
                <input
                placeholder="Search for movies"
                value="Spiderman"
                onChange={() => {}}
                />

                <img
                src={searchIcon}
                alt="search"
                onClick={() => {}}
                />
            </div>

            <div className="container">
                <div className="movie">
